import React, { useState } from 'react';
import './App.css';
import { BrowserRouter as Router, Route, Routes, Navigate  } from 'react-router-dom';
import Welcome from './Components/Welcome';
import SignUp from './Components/SignUp';
import Login from './Components/Login';
import VerifyCode from './Components/VerifyCode';
// import Success from './Components/Success';
import Home from './Components/Home';
import Revenues from './Components/Revenues';
import Expenses from './Components/Expenses';
import Profile from './Components/Profile';
import Budget from './Components/BudgetGoals';
import Report from './Components/Report';
import SessionTimeout from './Components/SessionTimeout';

// ProtectedRoute Component
const ProtectedRoute = ({ component: Component, setIsAuthenticated, ...rest }) => {
  const isAuthenticated = localStorage.getItem('isAuthenticated') === 'true'; // Or use state/context

  return (
    <Route
      {...rest}
      render={(props) =>
        isAuthenticated ? (
          <Component {...props} setIsAuthenticated={setIsAuthenticated} />
        ) : (
          <Navigate to="/login" />
        )
      }
    />
  );
};

function App() {
  const [isAuthenticated, setIsAuthenticated] = useState(localStorage.getItem('isAuthenticated') === 'true');

  return (
    <Router>
      {isAuthenticated && <SessionTimeout setIsAuthenticated={setIsAuthenticated} />}
      <Routes>
        <Route path="/" element={<Welcome />} />
        <Route path="/signup" element={<SignUp />} />
        <Route path="/login" element={<Login setIsAuthenticated={setIsAuthenticated} />} />
        <Route path="/verify" element={<VerifyCode setIsAuthenticated={setIsAuthenticated} />} />
        {/* <Route path="/success" element={<Success />} /> */}

        {/* Protected Routes */}
        <Route
          path="/home"
          element={<ProtectedRoute component={Home} setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/revenues"
          element={<ProtectedRoute component={Revenues} setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/expenses"
          element={<ProtectedRoute component={Expenses} setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/profile"
          element={<ProtectedRoute component={Profile} setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/budget"
          element={<ProtectedRoute component={Budget} setIsAuthenticated={setIsAuthenticated} />}
        />
        <Route
          path="/reports"
          element={<ProtectedRoute component={Report} setIsAuthenticated={setIsAuthenticated} />}
        />
      </Routes>
    </Router>
  );
}

export default App;
