import React, { useEffect, useState } from "react";
import { Link, NavLink } from "react-router-dom";
import '../Styling/Home.css';

function Home() {
    const [firstName, setFirstName] = useState('');

    useEffect(() => {
        // Retrieve the user's first name from localStorage when the component mounts
        const storedFirstName = localStorage.getItem('firstName');
        if (storedFirstName) {
            setFirstName(storedFirstName);
        }
    }, []);

    return (
        <div className="homeContainer">
            {/* Left Sidebar */}
            <div className="sidebar">
                <h1 className="appName">PerFinancial</h1>
                <hr className="divider" />
                
                <ul className="menu">
                    <li className="menuItem">
                        <NavLink to="/home" activeClassName="active">
                            <i className="fa fa-home" aria-hidden="true"></i>
                            <span>Home</span>
                        </NavLink>
                    </li>
                    <li className="menuItem">
                        <NavLink to="/revenues" activeClassName="active">
                            <i className="fa fa-arrow-up" aria-hidden="true"></i>
                            <span>Revenues</span>
                        </NavLink>
                    </li>
                    <li className="menuItem">
                        <NavLink to="/expenses" activeClassName="active">
                            <i className="fa fa-arrow-down" aria-hidden="true"></i>
                            <span>Expenses</span>
                        </NavLink>
                    </li>
                </ul>
                
                <div className="logoutSection">
                    <Link to="/">
                        <i class="fa fa-sign-out" aria-hidden="true"></i>
                        <span>Log Out</span>
                    </Link>
                </div>
            </div>

            {/* Right Content Area */}
            <div className="content">
                <h2>Welcome, {firstName}!</h2>
                
                {/* Placeholder for additional content */}
                <div className="summarySection">
                    <h3>Dashboard Summary</h3>
                    <div className="dashboard">
                        <div className="summary">
                            <h4>Total Revenues</h4>
                            <p>R60,000</p> {/* Placeholder value */}
                        </div>
                        <div className="summary">
                            <h4>Total Expenses</h4>
                            <p>R35,000</p> {/* Placeholder value */}
                        </div>
                        <div className="summary">
                            <h4>Net Balance</h4>
                            <p>R25,000</p> {/* Placeholder value */}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
