import React, { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "./Sidebar";
import '../Styling/Home.css';

function Home() {
    const [firstName, setFirstName] = useState('');
    const [surname, setSurname] = useState('');
    const [totalMonthlyRevenue, setTotalMonthlyRevenue] = useState(0);

    useEffect(() => {
        // Retrieve the user's first name and surname from localStorage when the component mounts
        const storedFirstName = localStorage.getItem('firstName');
        const storedSurname = localStorage.getItem('surname');
        const storedUserId = localStorage.getItem('id');

        if (storedFirstName) {
            setFirstName(storedFirstName);
        }
        if (storedSurname) {
            setSurname(storedSurname)
        }
        if (storedUserId) {
            fetchMonthlyRevenues(parseInt(storedUserId, 10));
        }
    }, []);

    // Function to fetch and calculate total revenues for the current month
    const fetchMonthlyRevenues = async (userId) => {
        try {
            // Fetch all revenues for the logged-in user
            const response = await axios.get(`http://localhost:8080/revenues/user/${userId}`);
            const allRevenues = response.data;

            // Get the current date and extract the current month and year
            const currentDate = new Date();
            const currentMonth = currentDate.getMonth();
            const currentYear = currentDate.getFullYear();

            // Filter revenues to only include those from the current month and year
            const monthlyRevenues = allRevenues.filter(revenue => {
                const revenueDate = new Date(revenue.date);
                return revenueDate.getMonth() === currentMonth && revenueDate.getFullYear() === currentYear;
            });

            // Calculate the total amount of monthly revenues
            const totalRevenue = monthlyRevenues.reduce((total, revenue) => total + revenue.amount, 0);

            setTotalMonthlyRevenue(totalRevenue); // Set the total revenue in state

        } catch (error) {
            console.error('Error fetching revenues: ', error);
        }
    };

    return (
        <div className="homeContainer">
            {/* Use Sidebar component */}
            <Sidebar />

            {/* Right Content Area */}
            <div className="content">
                <h2>Welcome, {firstName} {surname}!</h2>
                
                {/* Placeholder for additional content */}
                <div className="summarySection">
                    <h3>Dashboard Summary</h3>
                    <div className="dashboard">
                        <div className="summary">
                            <h4>
                                <i className="fa fa-arrow-up" aria-hidden="true"></i>
                                Total Revenues (Current Month)
                            </h4>
                            <p className="totalRevenue">R{totalMonthlyRevenue.toLocaleString()}</p>
                        </div>
                        <div className="summary">
                            <h4>
                                <i className="fa fa-arrow-down" aria-hidden="true"></i>
                                Total Expenses
                            </h4>
                            <p className="totalExpense">R35,000</p> {/* Placeholder value */}
                        </div>
                        <div className="summary">
                            <h4>
                                <i className="fa fa-check-circle" aria-hidden="true"></i>
                                Net Balance
                            </h4>
                            <p>R25,000</p> {/* Placeholder value */}
                        </div>
                    </div>
                </div>

                {/* New Section for Transactions and Monthly Statistics */}
                <div className="transactionsStatsSection">
                    <div className="transactions">
                        <h4>Transactions</h4>
                        <br/><br/>
                        {/* Placeholder for transaction content */}
                        <p>Details about transactions will go here.</p>
                    </div>
                    <div className="monthlyStatistics">
                        <h4>Monthly Statistics</h4>
                        <br/><br/>
                        {/* Placeholder for monthly statistics content */}
                        <p>Statistics for the month will go here.</p>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Home;
