import React, { useEffect, useState } from 'react';
import axios from 'axios';
import Sidebar from './Sidebar';
import TopNav from './TopNav';
import '../Styling/Profile.css';

function Profile() {
    const [user, setUser] = useState(null);
    const [editMode, setEditMode] = useState(false);
    const [formData, setFormData] = useState({
        firstName: '',
        surname: '',
        email: '',
        gender: '',
        dateOfBirth: ''
    });
    const [windowWidth, setWindowWidth] = useState(window.innerWidth);

    useEffect(() => {
        const userId = localStorage.getItem('id');

        // Fetch user data from API
        const fetchUserData = async () => {
            try {
                const response = await axios.get(`http://localhost:8080/users/${userId}`);
                setUser(response.data);
                setFormData({
                    firstName: response.data.firstName,
                    surname: response.data.surname,
                    email: response.data.email,
                    gender: response.data.gender,
                    dateOfBirth: response.data.dateOfBirth
                });
            } catch (error) {
                console.error('Error fetching user data: ', error);
            }
        };

        fetchUserData();
    }, []);

    // Track window resizing
    useEffect(() => {
        const handleResize = () => setWindowWidth(window.innerWidth);
        window.addEventListener('resize', handleResize);
        return () => window.removeEventListener('resize', handleResize);
    }, []);

    const handleInputChange = (e) => {
        const { name, value } = e.target;
        setFormData((prevData) => ({
            ...prevData,
            [name]: value
        }));
    };

    const handleSave = async () => {
        const userId = localStorage.getItem('id');

        try {
            await axios.put(`http://localhost:8080/users/${userId}`, formData);
            setEditMode(false); // Exit edit mode after saving
            setUser(formData);  // Update the displayed user data
        } catch (error) {
            console.error('Error updating user data: ', error);
        }
    };

    return (
        <div className="profileContainer">
            {/* Conditional rendering of Sidebar or TopNav based on window width */}
            {windowWidth > 650 ? <Sidebar /> : <TopNav />}

            <div className="profileContent">
                <h2>User Profile</h2>
                {user ? (
                    <>
                        {!editMode ? (
                            <div className="profileDetails">
                                <p><strong>First Name:</strong> {user.firstName}</p>
                                <p><strong>Surname:</strong> {user.surname}</p>
                                <p><strong>Email:</strong> {user.email}</p>
                                <p><strong>Gender:</strong> {user.gender}</p>
                                <p><strong>Date of Birth:</strong> {new Date(user.dateOfBirth).toLocaleDateString()}</p>
                                <button className="edit" onClick={() => setEditMode(true)}>Edit</button>
                            </div>
                        ) : (
                            <div className="profileEditForm">
                                <label>
                                    First Name:
                                    <input
                                        type="text"
                                        name="firstName"
                                        value={formData.firstName}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <label>
                                    Surname:
                                    <input
                                        type="text"
                                        name="surname"
                                        value={formData.surname}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <label>
                                    Email:
                                    <input
                                        type="email"
                                        name="email"
                                        value={formData.email}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <label>
                                    Gender:
                                    <input
                                        type="text"
                                        name="gender"
                                        value={formData.gender}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <label>
                                    Date of Birth:
                                    <input
                                        type="date"
                                        name="dateOfBirth"
                                        value={formData.dateOfBirth}
                                        onChange={handleInputChange}
                                    />
                                </label>
                                <button className="save" onClick={handleSave}>Save</button> {/* Save button */}
                                <button className="cancel" onClick={() => setEditMode(false)}>Cancel</button> {/* Cancel button */}
                            </div>
                        )}
                    </>
                ) : (
                    <p>Loading user data...</p>
                )}
            </div>
        </div>
    );
}

export default Profile;
