import React, { useEffect, useState } from "react";
import Sidebar from "./Sidebar";
import '../Styling/Revenues.css';

function Revenues() {
    const [revenues, setRevenues] = useState([]);

    useEffect(() => {
        // Fetch the user's revenue transactions from your backend (Placeholder for now)
        // Assuming you'd have an API call here to fetch data
        setRevenues([
            { id: 1, description: 'Salary', amount: 30000, date: '2024-10-01' },
            { id: 2, description: 'Freelance Work', amount: 15000, date: '2024-10-05' },
        ]);
    }, []);

    return (
        <div className="revenuesContainer">
            {/* Use Sidebar component */}
            <Sidebar />

            {/* Main content */}
            <div className="revenuesContent">
                {/* Revenues Section */}
                <div className="revenuesSection">
                    <h2>Revenues</h2>

                    {/* List of Revenues */}
                    <div className="revenuesList">
                        {revenues.map(revenue => (
                            <div key={revenue.id} className="revenueItem">
                                <p>{revenue.description}</p>
                                <p>R{revenue.amount}</p>
                                <p>{revenue.date}</p>
                            </div>
                        ))}
                    </div>
                </div>

                {/* Buttons Section */}
                <div className="buttonsSection">
                    <button className="addRevenueBtn">
                        <i className="fa fa-plus" aria-hidden="true"></i> Add Revenue
                    </button>
                    <button className="removeRevenueBtn">
                        <i className="fa fa-trash" aria-hidden="true"></i> Remove
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Revenues;
