import React, { useState, useEffect } from 'react';
import '../Styling/AddRevenueModal.css';

const AddRevenueModal = ({ isOpen, onClose, onAddRevenue }) => {
    const [amount, setAmount] = useState('');
    const [category, setCategory] = useState('');
    const [description, setDescription] = useState('');
    const [date, setDate] = useState('');
    const [userId, setUserId] = useState(null); // To store user ID

    // Fetch user ID from localStorage when the component mounts
    useEffect(() => {
        const storedUserId = localStorage.getItem('id'); // Assuming userId is stored in localStorage
        if (storedUserId) {
            setUserId(storedUserId);
        }
    }, []);

    const handleSubmit = (e) => {
        e.preventDefault();
        if (!userId) {
            console.error("User ID not available.");
            return;
        }

        // Prepare the revenue data object, including userId
        const revenueData = {
            amount: Number(amount),  // Ensure amount is a number
            category,
            description,
            date,
            user: {
                id: userId,  // Pass user ID as part of the revenue object
            },
        };

        onAddRevenue(revenueData); // Pass the revenue data to the parent handler
        onClose(); // Close the modal after submission
    };

    if (!isOpen) return null; // Don't render if modal is not open

    return (
        <div className="modal-overlay">
            <div className="modal-content">
                <h2>New Revenue Entry</h2>
                <form onSubmit={handleSubmit}>
                    <label>
                        Amount:
                        <input 
                            type="number" 
                            value={amount} 
                            onChange={(e) => setAmount(e.target.value)} 
                            required 
                        />
                    </label>
                    <label>
                        Category:
                        <input 
                            type="text" 
                            value={category} 
                            onChange={(e) => setCategory(e.target.value)} 
                            required 
                        />
                    </label>
                    <label>
                        Description:
                        <textarea 
                            value={description} 
                            onChange={(e) => setDescription(e.target.value)} 
                            required 
                        />
                    </label>
                    <label>
                        Date:
                        <input 
                            type="date" 
                            value={date} 
                            onChange={(e) => setDate(e.target.value)} 
                            required 
                        />
                    </label>
                    <button type="submit" className="submit">Add Revenue</button>
                    <button type="button" className="cancel" onClick={onClose}>Cancel</button>
                </form>
            </div>
        </div>
    );
};

export default AddRevenueModal;
