import React, { useEffect, useState } from "react";
import axios from "axios";
import Sidebar from "./Sidebar";
import '../Styling/Revenues.css';

function Revenues() {
    const [revenues, setRevenues] = useState([]);

    // Fetch revenue data when the component mounts
    useEffect(() => {
        const fetchRevenues = async () => {
            try {
                const response = await axios.get('http://localhost:8080/revenues');
                setRevenues(response.data); // Store revenue data in state
            } catch (error) {
                console.error('Error fetching revenues:', error);
            }
        };

        fetchRevenues();
    }, []);

    return (
        <div className="revenuesContainer">
            {/* Use Sidebar component */}
            <Sidebar />

            {/* Right Content Area */}
            <div className="revenuesContent">
                <h2>Revenues</h2>
                
                {/* Revenue Table Section */}
                <div className="revenuesTableContainer">
                    <table className="revenuesTable">
                        <thead>
                            <tr>
                                <th>Amount</th>
                                <th>Category</th>
                                <th>Description</th>
                                <th>Date</th>
                                {/* <th>User</th> */}
                            </tr>
                        </thead>
                        <tbody>
                            {revenues.length > 0 ? (
                                revenues.map((revenue) => (
                                    <tr key={revenue.id}>
                                        <td>R{revenue.amount}</td>
                                        <td>{revenue.category}</td>
                                        <td>{revenue.description}</td>
                                        <td>{new Date(revenue.date).toLocaleDateString()}</td>
                                        {/* <td>{revenue.user.firstName} {revenue.user.surname}</td> */}
                                    </tr>
                                ))
                            ) : (
                                <tr>
                                    <td colSpan="5">No revenues to display</td>
                                </tr>
                            )}
                        </tbody>
                    </table>
                </div>

                {/* Buttons Section */}
                <div className="revenueButtons">
                    <button className="addRevenueButton">
                        <i className="fa fa-plus"></i> Add revenue
                    </button>
                    <button className="removeRevenueButton">
                        <i className="fa fa-trash"></i> Remove
                    </button>
                </div>
            </div>
        </div>
    );
}

export default Revenues;
